'use strict';

// Import the Dialogflow module from the Actions on Google client library.
const {dialogflow} = require('actions-on-google');

// Import the firebase-functions package for deployment.
const functions = require('firebase-functions');

// Instantiate the Dialogflow client.
const app = dialogflow({debug: true});

let exclamations = ["Sure.", "Let me see.", "Alright.", "Okay."];
let goodbyes = ["Bye.", "Later.", "Okay. Call me again anytime.", "It was a pleasure to be of service"]

function randomGoodbye(){
	return goodbyes[Math.floor(Math.random() * goodbyes.length)]; 
}
function randomExclamation(){
	return exclamations[Math.floor(Math.random() * exclamations.length)];
}

function toTitleCase(str) {
  return str.replace(
    /\w\S*/g,
    function(txt) {
      return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
    }
  );
}

