let items = JSON.parse(items_str);

app.intent('DescribeItem', (conv) => {
    // Respond with the user's lucky number and end the conversation.
  	console.log(conv);
  	let itemName = conv.parameters.ItemName.toLowerCase();
    if(itemName in items){
      	let resp = items[itemName];
        conv.ask(toTitleCase(itemName) + ". " + resp + " Need help with anything else?"); 
    }
	else if(itemName){
    	conv.ask("I don't know the item called " + itemName + ". Need help with anything else?");
    }else{
    	conv.ask("That is not an item I know yet. Need help with anything else?");
    }
});

app.intent('FinishConversation', (conv) => {
	conv.close(randomGoodbye());
});

